import React, { useState } from 'react';
import './App.css';

function App() {
  const [jsonInput, setJsonInput] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState('');
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const parsedJson = JSON.parse(jsonInput);
      if (!parsedJson.data) {
        setError('Invalid JSON format: Missing "data" key');
        return;
      }
      setError('');
      const res = await fetch('http://127.0.0.1:5000/bfhl', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(parsedJson),
      });
      const result = await res.json();
      setResponse(result);
    } catch (err) {
      setError('Invalid JSON format');
    }
  };

  const handleOptionChange = (e) => {
    const { value, checked } = e.target;
    setSelectedOptions(prevOptions =>
      checked ? [...prevOptions, value] : prevOptions.filter(option => option !== value)
    );
  };

  const renderResponse = () => {
    if (!response) return null;
    const { numbers, alphabets, highest_alphabet } = response;
    return (
      <div>
        {selectedOptions.includes('Numbers') && (
          <div>
            <h3>Numbers</h3>
            <p>{numbers.join(', ')}</p>
          </div>
        )}
        {selectedOptions.includes('Alphabets') && (
          <div>
            <h3>Alphabets</h3>
            <p>{alphabets.join(', ')}</p>
          </div>
        )}
        {selectedOptions.includes('Highest Alphabet') && highest_alphabet.length > 0 && (
          <div>
            <h3>Highest Alphabet</h3>
            <p>{highest_alphabet.join(', ')}</p>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>RA2111030010155</h1>
        <form onSubmit={handleSubmit}>
          <textarea
            value={jsonInput}
            onChange={(e) => setJsonInput(e.target.value)}
            placeholder='Enter JSON here'
            rows="10"
            cols="50"
          />
          <br />
          <button type="submit">Submit</button>
        </form>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {response && (
          <>
            <h2>Response</h2>
            <div>
              <label>
                <input
                  type="checkbox"
                  value="Numbers"
                  onChange={handleOptionChange}
                />
                Numbers
              </label>
              <label>
                <input
                  type="checkbox"
                  value="Alphabets"
                  onChange={handleOptionChange}
                />
                Alphabets
              </label>
              <label>
                <input
                  type="checkbox"
                  value="Highest Alphabet"
                  onChange={handleOptionChange}
                />
                Highest Alphabet
              </label>
            </div>
            {renderResponse()}
          </>
        )}
      </header>
    </div>
  );
}

export default App;
